mapper.update:
        update person
        set fname = #{ fname }, minit = #{ minit }, lname = #{ lname }, username = #{ username }, province = #{ province }, city = #{ city },
            street = #{ street }, phone = #{ phone }
        where email = #{ email };

        update admin
        set status = #{ status }
        where email = #{ email }

update admin
set passwrd = #{ newPassword }
where email = #{ email } and passwrd = #{ password }

update admin
set passwrd = #{ newPassword }
where email = #{ email }

mapper.delete:
        delete from admin
        where email = #{email};

        delete from person
        where email = #{email}

mapper.select:
select *
from person p
where p.email in (
    select a.email
    from admin a
) and p.email = #{ email }
