mapper.update:
update borrow
set isbn = #{ isbn }, uDate = #{ uDate }
where email = #{ email }

mapper.select:
select b1.*, b2.name, b2.credit, p.username, p.phone, u.aCredit
from borrow b1, book b2, person p, user u
where b1.email = p.email and b1.email = u.email and b1.isbn = b2.isbn and b1.email = #{ email } and b1.isbn = #{ isbn }
