mapper.update:
        update person
        set fname = #{fname}, minit = #{minit}, lname = #{lname}, username = #{username}, province = #{province}, city = #{city},
            street = #{street}, phone = #{phone}
        where email = #{email};

        update user
        set gender = #{gender}, age = #{age}, uid = #{uid}, uTime = #{uTime}
        where email = #{email};

mapper.delete:
        delete from user
        where email = #{email};

        delete from person
        where email = #{email}
